  constructor(
    private publicService: PublicService,
    private http: HttpClient,
    private authService: AuthService,
    private commonService: CommonService
  ) {
    // this.doctorId = this.authService.getUserDetailsByKey('userParentId');
    this.doctorId = Constants.EMR_IPD_USER_DETAILS.docId;
    // this.locationId = this.authService.getUserDetailsByKey('defualt_location') ? this.authService.getUserDetailsByKey('defualt_location').location_id : null;
    this.locationId = Constants.EMR_IPD_USER_DETAILS.locationId;
  }

  getPatientAllConsultationFormDetails(patientId): Observable<any> {
    const formId = this.publicService.activeFormId;
    let patientConsultationDetails = this.filterPatientConsultationDetails(patientId, this.doctorId, this.locationId);
    if (patientConsultationDetails !== undefined && this.getActiveFormIndex(patientConsultationDetails.formData) !== -1) {
      return of(patientConsultationDetails);
    } else {
      const reqUrl = environment.STATIC_JSON_URL + 'consultation_data.json';
      return this.http.get(reqUrl).pipe(map((res: any) => {
        const result = res.data;

        if (patientConsultationDetails === undefined) {
          patientConsultationDetails = this.getDefaultPatConsultationFormObj(patientId);
          this.patientConsultationFormsArray.push(patientConsultationDetails);
        }

        const formDataObj = result;
        formDataObj['formId'] = formId;
        patientConsultationDetails.formData.push(result);
        // this.setLocalConsultationData(patientId, result);
      }));
    }
  }

  setLocalConsultationData(patientId, consultationData) {
    const consultationKeys = Object.keys(consultationData);
    const self = this;
    _.forEach(consultationKeys, function (key) {
      if (key === 'allergies') {
        // set allergies local data.
        self.setConsultationFormData(patientId, key, consultationData[key]);
      }
      // if (key === 'patient_diagnosis') {
      //   // set allergies local data.
      //   self.setConsultationFormData(patientId, key, consultationData[key]);
      // }
      // if (key === 'opd_tags') {
      //   // set allergies local data.
      //   self.getOpdTags();
      //   self.setConsultationFormData(patientId, key, consultationData[key]);
      // }
    });
  }

  setConsultationFormData(patientId, key, data, mode?, index?, noFormData?) {
    const formId = this.publicService.activeFormId;
    let activeFormData;
    const patientConsultationDetails = this.filterPatientConsultationDetails(patientId, this.doctorId, this.locationId);
    if (patientConsultationDetails === undefined) {
      // create patient default object and set form data.
      return this.addNewLocalPatientConsultationObj(patientId, key, data, formId, noFormData);
    }

    // check if key exist.
    if (!noFormData) {
      const formIndex = this.getActiveFormIndex(patientConsultationDetails.formData);
      if (formIndex === -1 && mode !== 'delete') {
        const formDataObj = {};
        formDataObj['formId'] = formId;
        formDataObj[key] = data;
        patientConsultationDetails.formData.push(formDataObj);
        return formDataObj[key];
      }
      activeFormData = patientConsultationDetails.formData[formIndex];
    } else {
      activeFormData = patientConsultationDetails;
    }

    if (activeFormData[key] === undefined && mode !== 'delete') {
      if (mode === 'add') {
        activeFormData[key] = [data];
      } else {
        activeFormData[key] = data;
      }
      return activeFormData[key];
    }

    if (_.isArray(activeFormData[key])) {
      if (mode === 'add') {
        activeFormData[key].push(data);
      } else if (mode === 'delete') {
        activeFormData[key].splice(index, 1);
      } else {
        activeFormData[key] = data;
      }
      return activeFormData[key];
    }

    if (_.isObject(activeFormData[key]) && (mode !== 'delete')) {
      activeFormData[key] = data;
      return activeFormData[key];
    }

    if (activeFormData[key] || activeFormData[key] === 0) {
      activeFormData[key] = data;
      return activeFormData[key];
    }
  }

  getConsultationFormDataByKey(patientId, key, noFormData?) {
    const patientConsultationDetails = this.filterPatientConsultationDetails(patientId, this.doctorId, this.locationId);
    let activeFormData;
    // console.log(patientConsultationDetails);
    if (patientConsultationDetails === undefined) {
      return false;
    }
    if (!noFormData) {
      const formIndex = this.getActiveFormIndex(patientConsultationDetails.formData);
      if (formIndex === -1) {
        return false;
      }
      activeFormData = patientConsultationDetails.formData[formIndex];
    } else {
      activeFormData = patientConsultationDetails;
    }

    return activeFormData[key] === undefined ? false : activeFormData[key];
  }


  getAllConsultationFormData(patientId) {
    const patientConsultationDetails = this.filterPatientConsultationDetails(patientId, this.doctorId, this.locationId);
    let activeFormData;
    // console.log(patientConsultationDetails);
    if (patientConsultationDetails === undefined) {
      return false;
    }
    activeFormData = patientConsultationDetails;
    return activeFormData

    // return activeFormData[key] === undefined ? false : activeFormData[key];
  }

  // check if form object is set for the patient.
  getActiveFormIndex(patientConsultationData) {
    const self = this;
    return _.findIndex(patientConsultationData, function (o) {
      return o.formId === self.publicService.activeFormId;
    });
  }

  getDefaultPatConsultationFormObj(patientId) {
    const defaultObj = new PatientConsultationDetails(this.doctorId, this.locationId, patientId, this.commonService.getLastActivePatient().ipdId);
    return _.cloneDeep(defaultObj);
  }

  filterPatientConsultationDetails(patientId, doctorId, locationId) {
    doctorId = doctorId ? doctorId : this.doctorId;
    locationId = locationId ? locationId : this.locationId;
    const ipdId = this.commonService.getLastActivePatient().ipdId;
    return _.find(this.patientConsultationFormsArray, (o) => {
      return o.patientId === patientId &&
        o.doctorId === doctorId &&
        o.ipdId == ipdId &&
        o.locationId === locationId;
    });
  }

  addNewLocalPatientConsultationObj(patientId, key, data, formId, noFormData?) {
    const patientDefaultData = this.getDefaultPatConsultationFormObj(patientId);
    if (!noFormData) {
      const formDataObj = {};
      formDataObj['formId'] = formId;
      formDataObj[key] = data;
      patientDefaultData.formData.push(formDataObj);
      this.patientConsultationFormsArray.push(patientDefaultData);
      return formDataObj[key];
    } else {
      patientDefaultData[key] = _.isArray(data) ? data : [data];
      this.patientConsultationFormsArray.push(patientDefaultData);
      return patientDefaultData[key];
    }
  }

  setPatientObj(key, val?) {
    if (_.isObject(key)) {
      this.patientObj = { ...key };
    } else {
      this.patientObj[key] = val;
    }
    this.patientObjSub.next(this.patientObj);
  }

  getPatientObj(key, returnObs?): any {
    if (this.patientObj.patientId) {
      return returnObs ? of(this.patientObj[key]) : this.patientObj[key];
    } else {
      return returnObs ?
        this.$patientObjSub.pipe(map(res => {
          return res[key];
        })
        ) : this.patientObj[key];
    }
  }

  setAdviceObj(val) {
    // if (_.isObject(key)) {
    //   this.adviceObj = { ...key };
    // } else {
    //   this.adviceObj[key] = val;
    // }
    this.adviceObjSub.next(val);
  }

